mapper.select:
SELECT

FROM t_cms_article st

WHERE st.id = #{id}

SELECT

FROM t_cms_article st


	
		AND st.columnsId = #{cm.columnsId}
	

ORDER BY st.stick desc

	limit ${cm.cmslimit}

SELECT

FROM t_cms_article st


	
		AND st.columnsId = #{cm.columnsId}
	
	
		AND st.status = #{cm.status}
	

order by st.stick desc

	limit ${cm.cmslimit}

SELECT

FROM t_cms_article st


	
		AND st.columnsId = #{cm.columnsId}
	

ORDER BY st.stick desc

	limit ${cm.cmslimit}

SELECT

FROM t_cms_article st


	
		AND st.columnsId = #{cm.columnsId}
	

ORDER BY st.stick desc

	limit ${cm.cmslimit}

SELECT
FROM t_cms_article st
AND st.columnsId = #{cm.columnsId}
AND ST.image is not null AND ST.status = 1
ORDER BY st.stick desc
limit ${cm.cmslimit}

mapper.update:
UPDATE t_cms_article SET
status = 1,
releasetor = #{releasetor}
WHERE id = #{id}
